import React, { useState, useEffect, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

const DroneMap = () => {
  const [droneData, setDroneData] = useState([]);
  const socketRef = useRef(null);

  useEffect(() => {
    // Fetch initial drone data from backend
    fetch("http://localhost:8000/fetch-drones")
      .then((response) => response.json())
      .then((data) => {
        if (data.drones) {
          setDroneData(data.drones);
        }
      })
      .catch((error) => console.error("‚ùå Error fetching drone data:", error));

    // Connect WebSocket for real-time updates
    socketRef.current = new WebSocket("ws://localhost:8000/ws");

    socketRef.current.onopen = () => {
      console.log("‚úÖ WebSocket Connected");
      socketRef.current.send(JSON.stringify({ message: "Hello from React!" }));
    };

    socketRef.current.onmessage = (event) => {
      try {
        const updatedData = JSON.parse(event.data);
        if (Array.isArray(updatedData) && updatedData.length > 0) {
          console.log("üì° Live Drone Update:", updatedData);
          setDroneData(updatedData);
        } else {
          console.warn("‚ö†Ô∏è Unexpected WebSocket Data:", updatedData);
        }
      } catch (error) {
        console.error("‚ùå Error parsing WebSocket data:", event.data);
      }
    };

    socketRef.current.onclose = () => {
      console.warn("‚ö†Ô∏è WebSocket Disconnected");
    };

    return () => {
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);

  // Function to set marker icons dynamically based on velocity
  const getMarkerIcon = (velocity) => {
    return L.icon({
      iconUrl: velocity > 50 ? "/assets/red_marker.png" : "/assets/green_marker.png",
      iconSize: [30, 40], // Adjust size as needed
      iconAnchor: [15, 40],
      popupAnchor: [0, -35],
    });
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>üöÄ <strong>Illegal Drone Traffic Tracking</strong></h2>
      <p>Monitoring real-time drone activity & alerts üõ∞Ô∏è</p>

      <MapContainer center={[37.7749, -122.4194]} zoom={5} style={{ height: "600px", width: "90%", margin: "auto" }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {droneData.map((drone, index) =>
          drone.latitude && drone.longitude ? (
            <Marker key={index} position={[drone.latitude, drone.longitude]} icon={getMarkerIcon(drone.velocity)}>
              <Popup>
                <strong>üöÅ Callsign: </strong>{drone.callsign}<br />
                <strong>üåç Country: </strong>{drone.country}<br />
                <strong>üìç Altitude: </strong>{drone.altitude} meters<br />
                <strong>üí® Velocity: </strong>{drone.velocity} km/h
              </Popup>
            </Marker>
          ) : null
        )}
      </MapContainer>
    </div>
  );
};

export default DroneMap;
