import React, { useState, useEffect, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./DroneMap.css";
import { FaTachometerAlt, FaExclamationTriangle, FaCog, FaEye, FaClock } from "react-icons/fa";

import redMarkerIcon from "../assets/red_marker.png";
import greenMarkerIcon from "../assets/green_marker.png";
import airportIconImg from "../assets/airport.png";
import militaryIconImg from "../assets/military.jpg";
import governmentIconImg from "../assets/government.jpg";

const DroneMap = () => {
    const [droneData, setDroneData] = useState([]);
    const [restrictedZones, setRestrictedZones] = useState([]);
    const [showLiveDetections, setShowLiveDetections] = useState(false);
    const [showSettings, setShowSettings] = useState(false);
    const [lastUpdated, setLastUpdated] = useState("");

    const socketRef = useRef(null);

    const handleToggleDetections = () => {
        setShowLiveDetections(!showLiveDetections);
    };

    // ‚úÖ Function to generate randomized drone data across all regions
    const generateRandomDrones = () => {
        const totalDrones = 15;
        const unauthorizedDrones = 5;
        let drones = [];

        for (let i = 0; i < totalDrones; i++) {
            const latitude = (Math.random() * 60 - 30).toFixed(4); // Random latitude within a broader range
            const longitude = (Math.random() * 150 - 75).toFixed(4); // Random longitude within a broader range
            const altitude = (Math.random() * 3000).toFixed(2);
            const velocity = (Math.random() * 250).toFixed(2);
            const unauthorized = i < unauthorizedDrones; // First 5 drones are unauthorized

            drones.push({
                callsign: `SIM-${i + 1}`,
                latitude: parseFloat(latitude),
                longitude: parseFloat(longitude),
                altitude: parseFloat(altitude),
                velocity: parseFloat(velocity),
                unauthorized: unauthorized,
            });
        }
        return drones;
    };

    useEffect(() => {
        const fetchDroneData = async () => {
            try {
                const data = generateRandomDrones();
                setDroneData(data);
                setLastUpdated(new Date().toLocaleTimeString());
            } catch (error) {
                console.error("Error fetching drone data:", error);
                setDroneData([]);
            }
        };

        const fetchRestrictedZones = async () => {
            try {
                const response = await fetch("http://127.0.0.1:8000/restricted-zones");
                const data = await response.json();
                setRestrictedZones(data.restricted_zones || []);
            } catch (error) {
                console.error("Error fetching restricted zones:", error);
                setRestrictedZones([]);
            }
        };

        fetchDroneData();
        fetchRestrictedZones();

        socketRef.current = new WebSocket("ws://localhost:8000/ws");

        socketRef.current.onopen = () => console.log("‚úÖ WebSocket Connected");

        socketRef.current.onmessage = (event) => {
            try {
                const receivedData = JSON.parse(event.data);
                if (receivedData.drones && Array.isArray(receivedData.drones)) {
                    setDroneData(receivedData.drones);
                    setLastUpdated(new Date().toLocaleTimeString());
                } else {
                    console.warn("‚ö†Ô∏è Unexpected WebSocket Data Format:", receivedData);
                }
            } catch (error) {
                console.error("‚ùå WebSocket Error Parsing Data:", event.data);
            }
        };

        socketRef.current.onerror = (error) => console.error("‚ùå WebSocket Error:", error);

        socketRef.current.onclose = () => {
            console.warn("‚ö†Ô∏è WebSocket Disconnected. Reconnecting...");
            setTimeout(() => {
                socketRef.current = new WebSocket("ws://localhost:8000/ws");
            }, 10000);
        };

        const interval = setInterval(fetchDroneData, 10000); // üîÑ Updates every 10 seconds

        return () => {
            clearInterval(interval);
            if (socketRef.current) {
                socketRef.current.close();
            }
        };
    }, []);

    const getZoneIcon = (zoneType) => {
        let iconImage;
        if (zoneType.includes("Airport")) iconImage = airportIconImg;
        else if (zoneType.includes("Base") || zoneType.includes("Fort")) iconImage = militaryIconImg;
        else iconImage = governmentIconImg;

        return L.icon({
            iconUrl: iconImage,
            iconSize: [40, 40],
            iconAnchor: [20, 40],
            popupAnchor: [0, -35],
        });
    };

    const getMarkerIcon = (drone) => {
        return L.icon({
            iconUrl: drone.unauthorized ? redMarkerIcon : greenMarkerIcon,
            iconSize: [30, 40],
            iconAnchor: [15, 40],
            popupAnchor: [0, -35],
        });
    };

    return (
        <div className="drone-dashboard">
            {/* üöÄ Navigation Bar */}
            <nav className="navbar">
                <h1 className="logo">üöÅ Illegal Drone Tracking System</h1>
                <div className="nav-links">
                    <button className="nav-button" onClick={handleToggleDetections}>
                        <FaEye /> Live Detections
                    </button>
                    <button className="nav-button" onClick={() => setShowSettings(true)}>
                        <FaCog /> Settings
                    </button>
                </div>
            </nav>

            {/* üó∫Ô∏è Full-Width Map */}
            <div className="map-container">
                <MapContainer
                    center={[37.7749, -99.4194]} // Adjusted to focus on broader US view
                    zoom={4} // Adjusted zoom to fit the whole region
                    style={{ height: "600px", width: "100%" }} // Increased height
                >
                    <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

                    {/* ‚úÖ Display Restricted Zones */}
                    {restrictedZones.map((zone, index) => (
                        <Marker key={index} position={[zone.latitude, zone.longitude]} icon={getZoneIcon(zone.name)}>
                            <Popup>
                                ‚ö†Ô∏è <strong>{zone.name}</strong>
                                <br /> Restricted Zone - Radius: {zone.radius} km
                            </Popup>
                        </Marker>
                    ))}

                    {/* ‚úÖ Display Drones */}
                    {droneData.map((drone, index) => (
                        <Marker key={index} position={[drone.latitude, drone.longitude]} icon={getMarkerIcon(drone)}>
                            <Popup>
                                üöÅ <strong>{drone.callsign || "Unknown"}</strong>
                                <br />üåç {drone.latitude.toFixed(2)}, {drone.longitude.toFixed(2)}
                                <br />üìç Altitude: {drone.altitude.toFixed(2)} meters
                                <br />üí® Velocity: {drone.velocity.toFixed(2)} km/h
                                <br />üî¥ Restricted Area: {drone.unauthorized ? "YES üö®" : "NO ‚úÖ"}
                            </Popup>
                        </Marker>
                    ))}
                </MapContainer>
            </div>

            {/* üì° Live Detections Modal */}
            {showLiveDetections && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close-button" onClick={() => setShowLiveDetections(false)}>√ó</span>
                        <h2>üì° Live Drone Detections</h2>
                        <p><FaClock /> Last Updated: {lastUpdated}</p>
                        <ul>
                            {droneData.map((drone, index) => (
                                <li key={index} className={drone.unauthorized ? "unauthorized-drone" : "authorized-drone"}>
                                    üöÅ <strong>{drone.callsign || "Unknown"}</strong> - Lat: {drone.latitude}, Lon: {drone.longitude}, Alt: {drone.altitude} m, Vel: {drone.velocity} km/h
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            )}
        </div>
    );
};

export default DroneMap;
