import React, { useState, useEffect, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./DroneMap.css";
import { FaTachometerAlt, FaExclamationTriangle, FaCog, FaEye, FaClock } from "react-icons/fa";

import redMarkerIcon from "../assets/red_marker.png";
import greenMarkerIcon from "../assets/green_marker.png";
import airportIconImg from "../assets/airport.png";
import militaryIconImg from "../assets/military.jpg";
import governmentIconImg from "../assets/government.jpg";

// Function to check if a drone is within a restricted zone
const isDroneInRestrictedZone = (drone, zones) => {
    return zones.some(zone => {
        const distance = Math.sqrt(
            Math.pow(drone.latitude - zone.latitude, 2) +
            Math.pow(drone.longitude - zone.longitude, 2)
        );
        return distance < zone.radius / 111; // Approximate conversion of km to degrees
    });
};

// Function to recenter the map based on detected drones
const RecenterMap = ({ droneData }) => {
    const map = useMap();

    useEffect(() => {
        if (droneData.length > 0) {
            const latitudes = droneData.map(drone => parseFloat(drone.latitude));
            const longitudes = droneData.map(drone => parseFloat(drone.longitude));

            const avgLat = latitudes.reduce((a, b) => a + b, 0) / latitudes.length;
            const avgLon = longitudes.reduce((a, b) => a + b, 0) / longitudes.length;

            map.setView([avgLat, avgLon], 5);
        }
    }, [droneData, map]);

    return null;
};

const DroneMap = () => {
    const [droneData, setDroneData] = useState([]);
    const [restrictedZones, setRestrictedZones] = useState([]);
    const [showLiveDetections, setShowLiveDetections] = useState(false);
    const [lastUpdated, setLastUpdated] = useState("");
    const socketRef = useRef(null);

    const handleToggleDetections = () => {
        setShowLiveDetections(!showLiveDetections);
    };

    // ‚úÖ Ensure restricted zones are fetched first, then generate drones
    useEffect(() => {
        const fetchRestrictedZones = async () => {
            try {
                const response = await fetch("http://127.0.0.1:8000/restricted-zones");
                const data = await response.json();
                setRestrictedZones(data.restricted_zones || []);
            } catch (error) {
                console.error("Error fetching restricted zones:", error);
                setRestrictedZones([]);
            }
        };

        fetchRestrictedZones();
    }, []);

    useEffect(() => {
        const fetchDroneData = () => {
            if (restrictedZones.length === 0) {
                console.warn("‚ö†Ô∏è Skipping drone generation: Restricted zones not loaded.");
                return;
            }

            const totalDrones = 15;
            let drones = [];

            for (let i = 0; i < totalDrones; i++) {
                const latitude = (Math.random() * (55 - 25) + 25).toFixed(4);
                const longitude = (Math.random() * (-70 + 125) - 125).toFixed(4);
                const altitude = (Math.random() * 3000).toFixed(2);
                const velocity = (Math.random() * 250).toFixed(2);

                const drone = {
                    callsign: `SIM-${i + 1}`,
                    latitude: parseFloat(latitude),
                    longitude: parseFloat(longitude),
                    altitude: parseFloat(altitude),
                    velocity: parseFloat(velocity),
                    unauthorized: false, // Default to authorized
                };

                if (isDroneInRestrictedZone(drone, restrictedZones)) {
                    drone.unauthorized = true; // ‚úÖ Mark as unauthorized if in a restricted zone
                }

                drones.push(drone);
            }

            setDroneData(drones);
            setLastUpdated(new Date().toLocaleTimeString());
        };

        fetchDroneData();
        const interval = setInterval(fetchDroneData, 10000);

        return () => clearInterval(interval);
    }, [restrictedZones]);

    const getZoneIcon = (zoneType) => {
        let iconImage;
        if (zoneType.includes("Airport")) iconImage = airportIconImg;
        else if (zoneType.includes("Base") || zoneType.includes("Fort")) iconImage = militaryIconImg;
        else iconImage = governmentIconImg;

        return L.icon({
            iconUrl: iconImage,
            iconSize: [40, 40],
            iconAnchor: [20, 40],
            popupAnchor: [0, -35],
        });
    };

    const getMarkerIcon = (drone) => {
        return L.icon({
            iconUrl: drone.unauthorized ? redMarkerIcon : greenMarkerIcon,
            iconSize: [30, 40],
            iconAnchor: [15, 40],
            popupAnchor: [0, -35],
        });
    };

    return (
        <div className="drone-dashboard">
            <nav className="navbar">
                <h1 className="logo">üöÅ Illegal Drone Tracking System</h1>
                <div className="nav-links">
                    <button className="nav-button" onClick={handleToggleDetections}>
                        <FaEye /> Live Detections
                    </button>
                    <button className="nav-button">
                        <FaCog /> Settings
                    </button>
                </div>
            </nav>

            <div className="map-container">
                <MapContainer
                    center={[39.8283, -98.5795]}
                    zoom={4}
                    style={{ height: "600px", width: "100%" }}
                >
                    <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                    <RecenterMap droneData={droneData} />

                    {restrictedZones.map((zone, index) => (
                        <Marker key={index} position={[zone.latitude, zone.longitude]} icon={getZoneIcon(zone.name)}>
                            <Popup>
                                ‚ö†Ô∏è <strong>{zone.name}</strong>
                                <br /> Restricted Zone - Radius: {zone.radius} km
                            </Popup>
                        </Marker>
                    ))}

                    {droneData.map((drone, index) => (
                        <Marker key={index} position={[drone.latitude, drone.longitude]} icon={getMarkerIcon(drone)}>
                            <Popup>
                                üöÅ <strong>{drone.callsign || "Unknown"}</strong>
                                <br />üåç {drone.latitude.toFixed(2)}, {drone.longitude.toFixed(2)}
                                <br />üìç Altitude: {drone.altitude.toFixed(2)} meters
                                <br />üí® Velocity: {drone.velocity.toFixed(2)} km/h
                                <br />üî¥ Restricted Area: {drone.unauthorized ? "YES üö®" : "NO ‚úÖ"}
                            </Popup>
                        </Marker>
                    ))}
                </MapContainer>
            </div>
        </div>
    );
};

export default DroneMap;
