import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import io from "socket.io-client";
import 'leaflet/dist/leaflet.css';

// WebSocket connection
const socket = io("http://localhost:8000/ws");

// Importing custom marker images
const redIcon = new L.Icon({
  iconUrl: "/red_marker.png",
  iconSize: [30, 40],
  iconAnchor: [15, 40],
  popupAnchor: [0, -35],
});

const greenIcon = new L.Icon({
  iconUrl: "/green_marker.png",
  iconSize: [30, 40],
  iconAnchor: [15, 40],
  popupAnchor: [0, -35],
});

const DroneMap = () => {
  const [droneData, setDroneData] = useState([]);

  // Fetch drones initially from API
  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/fetch-drones`)
      .then((response) => response.json())
      .then((data) => {
        if (data.drones) {
          setDroneData(data.drones);
        }
      })
      .catch((error) => console.error('Error fetching drone data:', error));
  }, []);

  // WebSocket listener for real-time updates
  useEffect(() => {
    socket.on("connect", () => {
      console.log("‚úÖ WebSocket Connected!");
    });

    socket.on("drone_update", (newDrones) => {
      console.log("üîÑ Live Drone Update:", newDrones);
      setDroneData(newDrones);
    });

    socket.on("disconnect", () => {
      console.log("‚ùå WebSocket Disconnected!");
    });

    return () => socket.disconnect();
  }, []);

  // Prevent reduce() error by checking if droneData is empty
  const avgVelocity = droneData.length > 0 
    ? droneData.reduce((sum, drone) => sum + drone.velocity, 0) / droneData.length
    : 0;  // Default to 0 if no data

  // Center map dynamically based on first drone's position
  const mapCenter = droneData.length > 0 
    ? [droneData[0].latitude, droneData[0].longitude] 
    : [37.7749, -122.4194]; // Default center (San Francisco)

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1 style={{ fontSize: "26px", fontWeight: "bold", color: "#333" }}>
        üöÄ Illegal Drone Traffic Tracking
      </h1>
      <p style={{ fontSize: "16px", color: "#666" }}>
        Monitoring real-time drone activity & alerts üöÅ
      </p>

      <MapContainer center={mapCenter} zoom={4} style={{ height: "600px", width: "90%", margin: "20px auto", borderRadius: "10px" }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {droneData.map((drone, index) => (
          <Marker
            key={index}
            position={[drone.latitude, drone.longitude]}
            icon={drone.velocity > 50 ? redIcon : greenIcon}
          >
            <Popup>
              <strong>üöÅ Callsign:</strong> {drone.callsign}<br />
              <strong>üåç Country:</strong> {drone.country}<br />
              <strong>üìç Latitude:</strong> {drone.latitude}<br />
              <strong>üìç Longitude:</strong> {drone.longitude}<br />
              <strong>üìè Altitude:</strong> {drone.altitude} meters<br />
              <strong>‚ö° Velocity:</strong> {drone.velocity} km/h
            </Popup>
          </Marker>
        ))}
      </MapContainer>

      <div style={{ padding: "15px", background: "#f4f4f4", marginTop: "15px", borderRadius: "10px", width: "90%", margin: "auto" }}>
        <h2>üìä Live Drone Updates</h2>
        <p><strong>üìà Avg Velocity:</strong> {avgVelocity.toFixed(2)} km/h</p>
        <ul style={{ listStyleType: "none", padding: 0 }}>
          {droneData.map((drone, index) => (
            <li key={index} style={{ marginBottom: "5px" }}>
              <strong>üöÅ {drone.callsign} ({drone.country})</strong> - Lat: {drone.latitude}, Lon: {drone.longitude}, Alt: {drone.altitude} meters, Vel: {drone.velocity} km/h
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default DroneMap;
