import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const DroneUpdates = () => {
  const [droneUpdates, setDroneUpdates] = useState([]);

  useEffect(() => {
    // Connect to WebSocket for real-time updates
    const socket = io(`${process.env.REACT_APP_API_URL}`);

    socket.on('drone-data-update', (data) => {
      setDroneUpdates((prevData) => [...prevData, data]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  return (
    <div>
      <h3>Drone Updates</h3>
      <ul>
        {droneUpdates.map((update, index) => (
          <li key={index}>
            {update.message} - Status: {update.status} at Lat: {update.lat}, Lon: {update.lon}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DroneUpdates;
