import React, { useState, useEffect, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./DroneMap.css";
import { LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer, Legend } from "recharts";
import { fetchDroneUpdates } from "./DroneUpdates";

const DroneMap = () => {
  const [droneData, setDroneData] = useState([]);
  const socketRef = useRef(null);

  useEffect(() => {
    fetch("http://localhost:8000/fetch-drones")
      .then((response) => response.json())
      .then((data) => {
        if (data.drones) {
            const correctedData = updatedData.map(drone => ({
                ...drone,
                altitude: drone.altitude ?? 0,   // Default to 0 if null
                velocity: drone.velocity ?? 0,   // Default to 0 if null
                timestamp: drone.timestamp < 10000000000 ? drone.timestamp * 1000 : drone.timestamp,
              }));              
          setDroneData(correctedData);
        }
      })
      .catch((error) => console.error("‚ùå Error fetching drone data:", error));

    socketRef.current = new WebSocket("ws://localhost:8000/ws");
    socketRef.current.onopen = () => {
      console.log("‚úÖ WebSocket Connected");
      socketRef.current.send(JSON.stringify({ message: "Hello from React!" }));
    };

    socketRef.current.onmessage = (event) => {
      try {
        const updatedData = JSON.parse(event.data);
        if (Array.isArray(updatedData) && updatedData.length > 0) {
          console.log("üì° Live Drone Update:", updatedData);
          const correctedData = updatedData.map(drone => ({
            ...drone,
            timestamp: drone.timestamp < 10000000000 ? drone.timestamp * 1000 : drone.timestamp,
          }));
          setDroneData(correctedData);
        } else {
          console.warn("‚ö†Ô∏è Unexpected WebSocket Data:", updatedData);
        }
      } catch (error) {
        console.error("‚ùå Error parsing WebSocket data:", event.data);
      }
    };

    socketRef.current.onclose = () => {
      console.warn("‚ö†Ô∏è WebSocket Disconnected");
    };

    return () => {
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);

  const getMarkerIcon = (velocity) => {
    return L.icon({
      iconUrl: velocity > 50 ? "/assets/red_marker.png" : "/assets/green_marker.png",
      iconSize: [30, 40],
      iconAnchor: [15, 40],
      popupAnchor: [0, -35],
    });
  };

  return (
    <div className="drone-container">
      <p>Monitoring real-time drone activity & alerts üõ∞Ô∏è</p>
      <MapContainer center={[37.7749, -122.4194]} zoom={3} style={{ height: "600px", width: "90%", margin: "auto" }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {droneData.map((drone, index) =>
          drone.latitude && drone.longitude ? (
            <Marker key={index} position={[drone.latitude, drone.longitude]} icon={getMarkerIcon(drone.velocity)}>
              <Popup>
                <strong>üöÅ Callsign: </strong>{drone.callsign}<br />
                <strong>üåç Country: </strong>{drone.country}<br />
                <strong>üìç Altitude: </strong>{drone.altitude ? drone.altitude.toFixed(2) : "N/A"} meters<br />
                <strong>üí® Velocity: </strong>{drone.velocity ? drone.velocity.toFixed(2) : "N/A"} km/h

              </Popup>
            </Marker>
          ) : null
        )}
      </MapContainer>
      <div className="drone-updates">
        <h3>üìä Live Drone Updates</h3>
        <p>‚ö° <strong>Avg Velocity:</strong> {droneData.length > 0 ? (droneData.reduce((sum, drone) => sum + drone.velocity, 0) / droneData.length).toFixed(2) : 0} km/h</p>
        <ul>
          {droneData.map((drone, index) => (
            <li key={index}>
              <strong>üöÅ {drone.callsign} ({drone.country})</strong> - Lat: {drone.latitude.toFixed(2)}, Lon: {drone.longitude.toFixed(2)}, Alt: {drone.altitude.toFixed(2)} meters, Vel: {drone.velocity.toFixed(2)} km/h
            </li>
          ))}
        </ul>
      </div>
      <div className="chart-container">
        <h2>Drone Speed & Altitude Trends</h2>
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={droneData}>
            <XAxis dataKey="timestamp" tickFormatter={(tick) => tick ? new Date(Number(tick)).toLocaleTimeString() : "N/A"} />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="speed" stroke="#8884d8" name="Speed (km/h)" dot={false} />
            <Line type="monotone" dataKey="altitude" stroke="#82ca9d" name="Altitude (m)" dot={false} />
            <Line type="monotone" dataKey="acceleration" stroke="#ff7300" name="Acceleration (m/s¬≤)" dot={false} />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default DroneMap;
