import React, { useState, useEffect, useRef } from "react";

const DroneUpdates = () => {
  const [droneList, setDroneList] = useState([]);
  const [lastUpdated, setLastUpdated] = useState(null);
  const socketRef = useRef(null);
  const updateIntervalRef = useRef(null);

  useEffect(() => {
    const connectWebSocket = () => {
      if (socketRef.current) {
        socketRef.current.close(); // Close any previous connections
      }

      socketRef.current = new WebSocket("ws://localhost:8000/ws");

      socketRef.current.onopen = () => {
        console.log("‚úÖ WebSocket Connected");
        socketRef.current.send(JSON.stringify({ message: "Requesting Drone Data" }));
      };

      socketRef.current.onmessage = (event) => {
        try {
          const updatedData = JSON.parse(event.data);
          console.log("üì° WebSocket Live Drone Data:", updatedData);

          if (updatedData.drones && Array.isArray(updatedData.drones)) {
            // Use the actual data from WebSocket
            setDroneList(updatedData.drones);
            setLastUpdated(new Date().toLocaleTimeString());
          } else {
            console.warn("‚ö†Ô∏è Unexpected WebSocket Data:", updatedData);
          }
        } catch (error) {
          console.error("‚ùå Error parsing WebSocket data:", event.data);
        }
      };

      socketRef.current.onclose = () => {
        console.warn("‚ö†Ô∏è WebSocket Disconnected. Reconnecting...");
        setTimeout(connectWebSocket, 10000); // Ensures reconnection after 10s
      };

      socketRef.current.onerror = (error) => {
        console.error("‚ùå WebSocket Error:", error);
      };
    };

    connectWebSocket();

    // ‚úÖ Enforce strict 10-second updates
    updateIntervalRef.current = setInterval(() => {
      if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
        socketRef.current.send(JSON.stringify({ message: "Request Drone Update" }));
      }
    }, 10000);

    return () => {
      if (socketRef.current) socketRef.current.close();
      clearInterval(updateIntervalRef.current);
    };
  }, []);

  return (
    <div className="drone-updates">
      <h2>üì° Live Drone Updates</h2>
      <p>üïí Last Updated: {lastUpdated || "Waiting for updates..."}</p>
      <ul>
        {droneList.length > 0 ? (
          droneList.map((drone, index) => (
            <li key={index} className={drone.unauthorized ? "unauthorized-drone" : "authorized-drone"}>
              üöÅ <strong>{drone.callsign}</strong> - üåç Lat: {drone.latitude}, 
              Lon: {drone.longitude} - üìè Alt: {drone.altitude} m - 
              üí® Vel: {drone.velocity} km/h - 
              {drone.unauthorized ? " ‚ùå Unauthorized" : " ‚úÖ Authorized"}
            </li>
          ))
        ) : (
          <p>üì° No live drone updates available.</p>
        )}
      </ul>
    </div>
  );
};

export default DroneUpdates;
