import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";

// Define custom marker icons
const redIcon = new L.Icon({
  iconUrl: "/assets/red_marker.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
});

const greenIcon = new L.Icon({
  iconUrl: "/assets/green_marker.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
});

const DroneMap = () => {
  const [droneData, setDroneData] = useState([]);

  useEffect(() => {
    // Fetch drone data from FastAPI
    fetch(`${process.env.REACT_APP_API_URL}/fetch-drones`)
      .then((response) => response.json())
      .then((data) => {
        if (data.drones) {
          setDroneData(data.drones);
        }
      })
      .catch((error) => console.error("Error fetching drone data: ", error));
  }, []);

  return (
    <MapContainer center={[37.7749, -122.4194]} zoom={5} style={{ height: "600px", width: "100%" }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      {droneData.map((drone, index) =>
        drone.latitude && drone.longitude ? (
          <Marker
            key={index}
            position={[drone.latitude, drone.longitude]}
            icon={drone.velocity > 50 ? redIcon : greenIcon}
          >
            <Popup>
              <strong>Callsign: </strong>{drone.callsign}<br />
              <strong>Country: </strong>{drone.country}<br />
              <strong>Altitude: </strong>{drone.altitude} meters<br />
              <strong>Velocity: </strong>{drone.velocity} km/h
            </Popup>
          </Marker>
        ) : null
      )}
    </MapContainer>
  );
};

export default DroneMap;
