import React, { useState, useEffect, useRef } from "react";

const DroneUpdates = () => {
  const [droneList, setDroneList] = useState([]);
  const socketRef = useRef(null);

  // ‚úÖ WebSocket Connection & Handling
  useEffect(() => {
    const connectWebSocket = () => {
      socketRef.current = new WebSocket("ws://localhost:8000/ws");

      socketRef.current.onopen = () => {
        console.log("‚úÖ WebSocket Connected");
        socketRef.current.send(JSON.stringify({ message: "Hello from React!" }));
      };

      socketRef.current.onmessage = (event) => {
        try {
          const updatedData = JSON.parse(event.data);
          console.log("üì° WebSocket Live Drone Data:", updatedData);

          if (updatedData.drones && Array.isArray(updatedData.drones)) {
            setDroneList(updatedData.drones);
          } else {
            console.warn("‚ö†Ô∏è Unexpected WebSocket Data:", updatedData);
          }
        } catch (error) {
          console.error("‚ùå Error parsing WebSocket data:", event.data);
        }
      };

      socketRef.current.onclose = () => {
        console.warn("‚ö†Ô∏è WebSocket Disconnected. Attempting Reconnection...");
        setTimeout(connectWebSocket, 3000); // üîÑ Auto-reconnect after 3 seconds
      };

      socketRef.current.onerror = (error) => {
        console.error("‚ùå WebSocket Error:", error);
      };
    };

    connectWebSocket();

    return () => {
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);

  return (
    <div className="drone-updates">
      <h2>üì° Live Drone Updates</h2>
      <ul>
        {droneList.length > 0 ? (
          droneList.map((drone, index) => (
            <li key={index} className={drone.unauthorized ? "unauthorized-drone" : "authorized-drone"}>
              üöÅ <strong>{drone.callsign || "Unknown"}</strong> - üåç Lat: {drone.latitude.toFixed(2)}, 
              Lon: {drone.longitude.toFixed(2)} - 
              {drone.unauthorized ? " ‚ùå Unauthorized" : " ‚úÖ Authorized"}
            </li>
          ))
        ) : (
          <p>üì° No live drone updates available.</p>
        )}
      </ul>
    </div>
  );
  console.log("üîç WebSocket Drone Data:", updatedData);

};

export default DroneUpdates;
