import React, { useState, useEffect, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./DroneMap.css";
import { FaTachometerAlt, FaExclamationTriangle, FaSearch } from "react-icons/fa";


const API_BASE_URL = "http://127.0.0.1:8000";

const DroneMap = () => {
    const [droneData, setDroneData] = useState([]);
    const [restrictedZones, setRestrictedZones] = useState([]);
    const socketRef = useRef(null);

    // ‚úÖ Fetch function to get API data
    const fetchData = async (endpoint, setState, stateKey) => {
        try {
            const response = await fetch(`${API_BASE_URL}/${endpoint}`);
            if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);
            const data = await response.json();
            if (data[stateKey]) setState(data[stateKey]);
        } catch (error) {
            console.error(`‚ùå Error fetching ${endpoint}:`, error);
            setState([]);
        }
    };

    // ‚úÖ Initialize data fetching and WebSocket connection
    useEffect(() => {
        fetchData("fetch-drones-live", setDroneData, "drones");
        fetchData("restricted-zones", setRestrictedZones, "restricted_zones");

        socketRef.current = new WebSocket("ws://localhost:8000/ws");

        socketRef.current.onopen = () => console.log("‚úÖ WebSocket Connected");

        socketRef.current.onmessage = (event) => {
            try {
                const updatedData = JSON.parse(event.data);
                if (Array.isArray(updatedData.drones)) {
                    setDroneData([...updatedData.drones]); // ‚úÖ Force re-render
                } else {
                    console.warn("‚ö†Ô∏è Unexpected WebSocket Data Format:", updatedData);
                }
            } catch (error) {
                console.error("‚ùå Error parsing WebSocket data:", event.data);
            }
        };

        socketRef.current.onerror = (error) => console.error("‚ùå WebSocket Error:", error);

        socketRef.current.onclose = () => {
            console.warn("‚ö†Ô∏è WebSocket Disconnected. Attempting Reconnection...");
            setTimeout(() => {
                socketRef.current = new WebSocket("ws://localhost:8000/ws");
            }, 3000);
        };

        return () => {
            if (socketRef.current) socketRef.current.close();
        };
    }, []);

    useEffect(() => console.log("üõ∞ Updated Drone Data:", droneData), [droneData]);

    // ‚úÖ Marker icons for drones & restricted zones
    const getMarkerIcon = (drone) =>
        L.icon({
            iconUrl: drone.unauthorized ? "/assets/red_marker.png" : "/assets/green_marker.png",
            iconSize: [30, 40],
            iconAnchor: [15, 40],
            popupAnchor: [0, -35],
            className: "custom-drone-marker",
        });

    const getZoneIcon = (zoneName) => {
        let iconImage =
            zoneName.includes("Airport") ? "/assets/airport.png" :
            zoneName.includes("Base") || zoneName.includes("Fort") ? "/assets/military.jpg" :
            "/assets/government.jpg";

        return L.icon({
            iconUrl: iconImage,
            iconSize: [40, 40],
            iconAnchor: [20, 40],
            popupAnchor: [0, -35],
        });
    };

    return (
        <div className="drone-dashboard">
            {/* üöÄ Navigation Bar */}
            <nav className="navbar">
                <h1 className="logo">üöÅ Illegal Drone Tracking System</h1>
                <div className="nav-links">
                    <button className="nav-button">Live Detections</button>
                    <button className="nav-button">Settings</button>
                </div>
            </nav>

            {/* üè∑Ô∏è Header */}
            <div className="header">
                üöÄ <strong>Tracking Unauthorized Drone Activity in Real-Time</strong> üõ∞Ô∏è
            </div>

            {/* üó∫Ô∏è Full-Width Map */}
            <div className="map-container">
                <MapContainer center={[37.7749, -122.4194]} zoom={5} style={{ height: "500px", width: "100%" }}>
                    <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

                    {restrictedZones.map((zone, index) => (
                        <Marker key={index} position={[zone.latitude, zone.longitude]} icon={getZoneIcon(zone.name)}>
                            <Popup>
                                ‚ö†Ô∏è <strong>{zone.name}</strong>
                                <br /> Restricted Zone - Radius: {zone.radius} km
                            </Popup>
                        </Marker>
                    ))}

                    {droneData.map((drone, index) => (
                        <Marker key={index} position={[drone.latitude, drone.longitude]} icon={getMarkerIcon(drone)}>
                            <Popup>
                                üöÅ <strong>{drone.callsign || "Unknown"}</strong>
                                <br />üåç {drone.latitude.toFixed(2)}, {drone.longitude.toFixed(2)}
                                <br />üìç Altitude: {drone.altitude.toFixed(2)} meters
                                <br />üí® Velocity: {drone.velocity.toFixed(2)} km/h
                                <br />üî¥ Restricted Area: {drone.unauthorized ? "YES üö®" : "NO ‚úÖ"}
                            </Popup>
                        </Marker>
                    ))}
                </MapContainer>
            </div>

            {/* üìä Real-Time Statistics Panel BELOW the Map */}
            <div className="stats-panel">
                <h2 className="stats-title">üìä Real-Time Drone Statistics</h2>
                <div className="stats-container">
                    <div className="stats-card">
                        <h3>üöÄ Total Drones</h3>
                        <p>{droneData.length}</p>
                    </div>
                    <div className="stats-card unauthorized">
                        <h3>‚ö†Ô∏è Unauthorized Drones</h3>
                        <p>{droneData.filter(d => d.unauthorized).length}</p>
                    </div>
                    <div className="stats-card">
                        <h3>üìà Avg Speed</h3>
                        <p>{droneData.length > 0 ? (droneData.reduce((sum, d) => sum + d.velocity, 0) / droneData.length).toFixed(2) : 0} km/h</p>
                    </div>
                    <div className="stats-card">
                        <h3>‚õ∞Ô∏è Avg Altitude</h3>
                        <p>{droneData.length > 0 ? (droneData.reduce((sum, d) => sum + d.altitude, 0) / droneData.length).toFixed(2) : 0} m</p>
                    </div>
                </div>
            </div>

            {/* üì° Live Drone Updates */}
            <div className="drone-updates">
                <h3>üìä Live Drone Updates</h3>
                <ul>
                    {droneData.map((drone, index) => (
                        <li key={index} className={drone.unauthorized ? "unauthorized-drone" : "authorized-drone"}>
                            üöÅ <strong>{drone.callsign || "Unknown"}</strong> - Lat: {drone.latitude?.toFixed(2) || "N/A"}, 
                            Lon: {drone.longitude?.toFixed(2) || "N/A"}, Alt: {drone.altitude?.toFixed(2) || "N/A"} m, 
                            Vel: {drone.velocity?.toFixed(2) || "N/A"} km/h
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default DroneMap;
