import React, { useState, useEffect, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./DroneMap.css";
import { FaEye, FaClock } from "react-icons/fa";

// Marker icons
import redMarkerIcon from "../assets/red_marker.png";
import greenMarkerIcon from "../assets/green_marker.png";
import airportIconImg from "../assets/airport.png";
import militaryIconImg from "../assets/military.jpg";
import governmentIconImg from "../assets/government.jpg";

const DroneMap = () => {
  const [droneData, setDroneData] = useState([]);
  const [restrictedZones, setRestrictedZones] = useState([]);
  const [showLiveDetections, setShowLiveDetections] = useState(false);
  const [lastUpdated, setLastUpdated] = useState("");
  const socketRef = useRef(null);

  // -- Distance Helpers --
  const toRad = (val) => (val * Math.PI) / 180;
  const haversineDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Earth radius in km
    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(toRad(lat1)) *
        Math.cos(toRad(lat2)) *
        Math.sin(dLon / 2) ** 2;
    return R * 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  };

  // Mark drone as unauthorized with reason if inside any restricted zone
  const applyRestrictedZoneLogic = (drone, zones) => {
    for (const zone of zones) {
      const dist = haversineDistance(
        drone.latitude,
        drone.longitude,
        zone.latitude,
        zone.longitude
      );
      // If distance < zone.radius => unauthorized
      if (dist <= zone.radius) {
        drone.unauthorized = true;
        drone.reason = "Restricted Zone";
        return drone; // once flagged, stop
      }
    }
    // If not in any zone, no changes
    return drone;
  };

  // Generate random drones (some unauthorized)
  const generateRandomDrones = (count) => {
    const drones = [];
    for (let i = 0; i < count; i++) {
      drones.push({
        callsign: `SIM-${i + 1}`,
        latitude: getRandomInRange(25, 49),     // US lat range
        longitude: getRandomInRange(-125, -67), // US lon range
        altitude: getRandomInRange(100, 3000),
        velocity: getRandomInRange(30, 200),
        unauthorized: Math.random() < 0.4,      // ~40% chance unauthorized
        reason: "", // We can fill this later if restricted
      });
    }
    return drones;
  };

  const getRandomInRange = (min, max) => {
    return parseFloat((Math.random() * (max - min) + min).toFixed(6));
  };

  // -- Fetch & WebSocket Setup --
  useEffect(() => {
    const fetchDroneData = async () => {
      try {
        // 1) Fetch real server drones
        const response = await fetch("http://127.0.0.1:8000/fetch-drones-live");
        const data = await response.json();
        let realDrones = data.drones || [];

        // 2) If no unauthorized in real data, generate 5 random unauthorized
        const unauthorizedCount = realDrones.filter((d) => d.unauthorized).length;
        if (unauthorizedCount === 0) {
          const randomUnauthorized = generateRandomDrones(5);
          // Force them to be unauthorized
          randomUnauthorized.forEach((dr) => {
            dr.unauthorized = true;
            dr.reason = "No server data unauthorized (simulated)";
          });
          realDrones = realDrones.concat(randomUnauthorized);
        }

        setDroneData(realDrones);
        setLastUpdated(new Date().toLocaleTimeString());
      } catch (error) {
        console.error("Error fetching drone data:", error);
        // If server fails, fallback to random
        const fallback = generateRandomDrones(10);
        setDroneData(fallback);
        setLastUpdated(new Date().toLocaleTimeString());
      }
    };

    const fetchRestrictedZones = async () => {
      try {
        const response = await fetch("http://127.0.0.1:8000/restricted-zones");
        const data = await response.json();
        setRestrictedZones(data.restricted_zones || []);
      } catch (error) {
        console.error("Error fetching restricted zones:", error);
        setRestrictedZones([]);
      }
    };

    fetchDroneData();
    fetchRestrictedZones();

    // WebSocket
    socketRef.current = new WebSocket("ws://localhost:8000/ws");
    socketRef.current.onopen = () => {
      console.log("✅ WebSocket Connected");
    };

    socketRef.current.onmessage = (event) => {
      try {
        const receivedData = JSON.parse(event.data);
        if (receivedData.drones && Array.isArray(receivedData.drones)) {
          let updated = receivedData.drones;
          // If no unauthorized from server, add 5 random unauthorized
          const unauthorizedCount = updated.filter((d) => d.unauthorized).length;
          if (unauthorizedCount === 0) {
            const randomUnauthorized = generateRandomDrones(5);
            randomUnauthorized.forEach((dr) => {
              dr.unauthorized = true;
              dr.reason = "No server data unauthorized (simulated)";
            });
            updated = updated.concat(randomUnauthorized);
          }
          setDroneData(updated);
          setLastUpdated(new Date().toLocaleTimeString());
        } else {
          console.warn("⚠️ Unexpected WebSocket Data Format:", receivedData);
        }
      } catch (error) {
        console.error("❌ WebSocket Error Parsing Data:", event.data);
      }
    };

    socketRef.current.onerror = (error) => {
      console.error("❌ WebSocket Error:", error);
    };

    socketRef.current.onclose = () => {
      console.warn("⚠️ WebSocket Disconnected. Reconnecting...");
      setTimeout(() => {
        socketRef.current = new WebSocket("ws://localhost:8000/ws");
      }, 10000);
    };

    return () => {
      if (socketRef.current) socketRef.current.close();
    };
  }, []);

  // -- Re-apply restricted zone logic whenever data or zones change
  useEffect(() => {
    if (!droneData.length || !restrictedZones.length) return;

    // For each drone, check if in restricted zone => set unauthorized + reason
    const updatedDrones = droneData.map((drone) =>
      applyRestrictedZoneLogic(drone, restrictedZones)
    );
    setDroneData(updatedDrones);
  }, [droneData, restrictedZones]);

  // Marker icons
  const getZoneIcon = (zoneType) => {
    let iconImage;
    if (zoneType.includes("Airport")) iconImage = airportIconImg;
    else if (zoneType.includes("Base") || zoneType.includes("Fort")) iconImage = militaryIconImg;
    else iconImage = governmentIconImg;
    return L.icon({
      iconUrl: iconImage,
      iconSize: [40, 40],
      iconAnchor: [20, 40],
      popupAnchor: [0, -35],
    });
  };

  const getMarkerIcon = (drone) => {
    return L.icon({
      iconUrl: drone.unauthorized ? redMarkerIcon : greenMarkerIcon,
      iconSize: [30, 40],
      iconAnchor: [15, 40],
      popupAnchor: [0, -35],
    });
  };

  // Modal
  const handleToggleDetections = () => {
    setShowLiveDetections(true);
  };
  const handleCloseModal = () => {
    setShowLiveDetections(false);
  };

  // -- Render
  return (
    <div className="drone-dashboard">
      <nav className="navbar">
        <div className="navbar-brand">
          <h2 className="brand-title">Airspace Security Monitor</h2>
        </div>
        <div className="navbar-right">
          <button className="nav-button" onClick={handleToggleDetections}>
            <FaEye /> Live Detections
          </button>
        </div>
      </nav>

      <div className="map-container">
        <MapContainer center={[37.7749, -99.4194]} zoom={4} style={{ height: "600px", width: "100%" }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

          {/* Restricted Zones */}
          {restrictedZones.map((zone, index) => (
            <Marker key={index} position={[zone.latitude, zone.longitude]} icon={getZoneIcon(zone.name)}>
              <Popup>
                ⚠️ <strong>{zone.name}</strong>
                <br /> Restricted Zone - Radius: {zone.radius} km
              </Popup>
            </Marker>
          ))}

          {/* Drones (some may be unauthorized => red icon) */}
          {droneData.map((drone, index) => (
            <Marker key={index} position={[drone.latitude, drone.longitude]} icon={getMarkerIcon(drone)}>
              <Popup>
                <strong>{drone.callsign || "Unknown"}</strong>
                <br />
                Lat: {drone.latitude.toFixed(2)}, Lon: {drone.longitude.toFixed(2)}
                <br />
                Alt: {drone.altitude.toFixed(2)} m, Vel: {drone.velocity.toFixed(2)} km/h
                <br />
                {drone.unauthorized
                  ? `❌ Unauthorized (${drone.reason || "No Reason Provided"})`
                  : "✅ Authorized"}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* Live Detections Modal */}
      {showLiveDetections && (
        <div className="modal">
          <div className="modal-content">
            <span className="close-button" onClick={handleCloseModal}>
              &times;
            </span>
            <h2>📡 Live Drone Detections</h2>
            <p>
              <FaClock /> Last Updated: {lastUpdated}
            </p>
            <div className="stats-container">
              <div className="stats-card">
                <h3>Total Drones</h3>
                <p>{droneData.length}</p>
              </div>
              <div className="stats-card unauthorized">
                <h3>Unauthorized Drones</h3>
                <p>{droneData.filter((d) => d.unauthorized).length}</p>
              </div>
            </div>
            <ul>
              {droneData.map((drone, index) => (
                <li key={index} className={drone.unauthorized ? "unauthorized-drone" : "authorized-drone"}>
                  <strong>{drone.callsign || "Unknown"}</strong> - Lat: {drone.latitude.toFixed(2)}, Lon:{" "}
                  {drone.longitude.toFixed(2)}, Alt: {drone.altitude.toFixed(2)} m, Vel:{" "}
                  {drone.velocity.toFixed(2)} km/h,{" "}
                  {drone.unauthorized
                    ? `❌ Unauthorized (${drone.reason || "No Reason Provided"})`
                    : "✅ Authorized"}
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default DroneMap;
