import React, { useState, useEffect, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./DroneMap.css";
import { FaEye, FaClock } from "react-icons/fa";

import redMarkerIcon from "../assets/red_marker.png";
import greenMarkerIcon from "../assets/green_marker.png";
import airportIconImg from "../assets/airport.png";
import militaryIconImg from "../assets/military.jpg";
import governmentIconImg from "../assets/government.jpg";

const DroneMap = () => {
  // State to hold fetched + random drone data, restricted zones, etc.
  const [droneData, setDroneData] = useState([]);
  const [restrictedZones, setRestrictedZones] = useState([]);
  const [showLiveDetections, setShowLiveDetections] = useState(false);
  const [lastUpdated, setLastUpdated] = useState("");

  // WebSocket reference for live updates
  const socketRef = useRef(null);

  // Toggle the live detections modal
  const handleToggleDetections = () => {
    setShowLiveDetections((prev) => !prev);
  };

  //-------------------------------------------------------------------
  // 1) Function to generate random drones (authorized + unauthorized)
  //-------------------------------------------------------------------
  const generateRandomDrones = (count) => {
    const drones = [];
    for (let i = 0; i < count; i++) {
      drones.push({
        callsign: `SIM-${i + 1}`,
        latitude: getRandomInRange(25, 49),     // US lat range
        longitude: getRandomInRange(-125, -67), // US lon range
        altitude: getRandomInRange(100, 3000),
        velocity: getRandomInRange(30, 200),
        unauthorized: Math.random() < 0.4,      // ~40% chance unauthorized
      });
    }
    return drones;
  };

  const getRandomInRange = (min, max) => {
    return parseFloat((Math.random() * (max - min) + min).toFixed(6));
  };

  //-------------------------------------------------------------------
  // 2) Fetch data & set up WebSocket
  //-------------------------------------------------------------------
  useEffect(() => {
    // Fetch real drone data from your API
    const fetchDroneData = async () => {
      try {
        const response = await fetch("http://127.0.0.1:8000/fetch-drones-live");
        const data = await response.json();

        // Combine server drones with random drones
        const randomDrones = generateRandomDrones(5);
        const combinedDrones = [...(data.drones || []), ...randomDrones];

        setDroneData(combinedDrones);
        setLastUpdated(new Date().toLocaleTimeString());
      } catch (error) {
        console.error("Error fetching drone data:", error);
        // If the server fails, just show random drones
        const fallbackRandom = generateRandomDrones(10);
        setDroneData(fallbackRandom);
      }
    };

    // Fetch restricted zones
    const fetchRestrictedZones = async () => {
      try {
        const response = await fetch("http://127.0.0.1:8000/restricted-zones");
        const data = await response.json();
        setRestrictedZones(data.restricted_zones || []);
      } catch (error) {
        console.error("Error fetching restricted zones:", error);
        setRestrictedZones([]);
      }
    };

    // Perform initial fetch
    fetchDroneData();
    fetchRestrictedZones();

    // Set up WebSocket for live updates
    socketRef.current = new WebSocket("ws://localhost:8000/ws");

    socketRef.current.onopen = () => {
      console.log("‚úÖ WebSocket Connected");
    };

    socketRef.current.onmessage = (event) => {
      try {
        const receivedData = JSON.parse(event.data);

        if (receivedData.drones && Array.isArray(receivedData.drones)) {
          // Merge random drones with incoming WebSocket drones
          const randomDrones = generateRandomDrones(3); // or any number you want
          const combinedDrones = [...receivedData.drones, ...randomDrones];

          setDroneData(combinedDrones);
          setLastUpdated(new Date().toLocaleTimeString());
        } else {
          console.warn("‚ö†Ô∏è Unexpected WebSocket Data Format:", receivedData);
        }
      } catch (error) {
        console.error("‚ùå WebSocket Error Parsing Data:", event.data);
      }
    };

    socketRef.current.onerror = (error) => {
      console.error("‚ùå WebSocket Error:", error);
    };

    socketRef.current.onclose = () => {
      console.warn("‚ö†Ô∏è WebSocket Disconnected. Reconnecting...");
      setTimeout(() => {
        socketRef.current = new WebSocket("ws://localhost:8000/ws");
      }, 10000);
    };

    // Cleanup on unmount
    return () => {
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);

  //-------------------------------------------------------------------
  // 3) Marker Icon Functions
  //-------------------------------------------------------------------
  // Return a custom Leaflet icon for restricted zones
  const getZoneIcon = (zoneType) => {
    let iconImage;
    if (zoneType.includes("Airport")) {
      iconImage = airportIconImg;
    } else if (zoneType.includes("Base") || zoneType.includes("Fort")) {
      iconImage = militaryIconImg;
    } else {
      iconImage = governmentIconImg;
    }
    return L.icon({
      iconUrl: iconImage,
      iconSize: [40, 40],
      iconAnchor: [20, 40],
      popupAnchor: [0, -35],
    });
  };

  // Return a custom marker icon for a drone
  const getMarkerIcon = (drone) => {
    return L.icon({
      iconUrl: drone.unauthorized ? redMarkerIcon : greenMarkerIcon,
      iconSize: [30, 40],
      iconAnchor: [15, 40],
      popupAnchor: [0, -35],
    });
  };

  //-------------------------------------------------------------------
  // 4) Render
  //-------------------------------------------------------------------
  return (
    <div className="drone-dashboard">
      {/* Minimal Navigation Bar */}
      <nav className="navbar">
        <div className="navbar-brand">
          <h2 className="brand-title">Airspace Security Monitor</h2>
        </div>
        <div className="navbar-right">
          <button className="nav-button" onClick={handleToggleDetections}>
            <FaEye /> Live Detections
          </button>
        </div>
      </nav>

      {/* Full-Width Map */}
      <div className="map-container">
        <MapContainer
          center={[37.7749, -99.4194]} // Broader US view
          zoom={4}
          style={{ height: "600px", width: "100%" }}
        >
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

          {/* Render restricted zones */}
          {restrictedZones.map((zone, index) => (
            <Marker
              key={index}
              position={[zone.latitude, zone.longitude]}
              icon={getZoneIcon(zone.name)}
            >
              <Popup>
                ‚ö†Ô∏è <strong>{zone.name}</strong>
                <br /> Restricted Zone - Radius: {zone.radius} km
              </Popup>
            </Marker>
          ))}

          {/* Render drones (merged real + random) */}
          {droneData.map((drone, index) => (
            <Marker
              key={index}
              position={[drone.latitude, drone.longitude]}
              icon={getMarkerIcon(drone)}
            >
              <Popup>
                <strong>{drone.callsign}</strong>
                <br />Lat: {drone.latitude.toFixed(2)}, Lon: {drone.longitude.toFixed(2)}
                <br />Alt: {drone.altitude.toFixed(2)} m, Vel: {drone.velocity.toFixed(2)} km/h
                <br />Status: {drone.unauthorized ? "‚ùå Unauthorized" : "‚úÖ Authorized"}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* Live Detections Modal */}
      {showLiveDetections && (
        <div className="modal">
          <div className="modal-content">
            <span className="close-button" onClick={() => setShowLiveDetections(false)}>
              &times;
            </span>
            <h2>üì° Live Drone Detections</h2>
            <p>
              <FaClock /> Last Updated: {lastUpdated}
            </p>
            <div className="stats-container">
              <div className="stats-card">
                <h3>Total Drones</h3>
                <p>{droneData.length}</p>
              </div>
              <div className="stats-card unauthorized">
                <h3>Unauthorized Drones</h3>
                <p>{droneData.filter((d) => d.unauthorized).length}</p>
              </div>
            </div>
            <ul>
              {droneData.map((drone, index) => (
                <li
                  key={index}
                  className={drone.unauthorized ? "unauthorized-drone" : "authorized-drone"}
                >
                  <strong>{drone.callsign}</strong> - Lat: {drone.latitude.toFixed(2)}, Lon:{" "}
                  {drone.longitude.toFixed(2)}, Alt: {drone.altitude.toFixed(2)} m, Vel:{" "}
                  {drone.velocity.toFixed(2)} km/h, Status:{" "}
                  {drone.unauthorized ? "‚ùå Unauthorized" : "‚úÖ Authorized"}
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default DroneMap;
