import React, { useState, useEffect, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./DroneMap.css";

import StatsPanel from "./StatsPanel"; // ✅ Import the StatsPanel component

import redMarkerIcon from "../assets/red_marker.png";
import greenMarkerIcon from "../assets/green_marker.png";
import airportIconImg from "../assets/airport.png";
import militaryIconImg from "../assets/military.jpg";
import governmentIconImg from "../assets/government.jpg";

const API_BASE_URL = "http://127.0.0.1:8000";

const DroneMap = () => {
  const [droneData, setDroneData] = useState([]);
  const [restrictedZones, setRestrictedZones] = useState([]);
  const socketRef = useRef(null);

  // ✅ Fetch function to get data from the API
  const fetchData = async (endpoint, setState, stateKey) => {
    try {
      const response = await fetch(`${API_BASE_URL}/${endpoint}`);

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      if (data[stateKey]) {
        setState(data[stateKey]);
      }
    } catch (error) {
      console.error(`❌ Error fetching ${endpoint}:`, error);
      setState([]);
    }
  };

  // ✅ Initialize data fetching and WebSocket connection
  useEffect(() => {
    fetchData("fetch-drones-live", setDroneData, "drones");
    fetchData("restricted-zones", setRestrictedZones, "restricted_zones");

    socketRef.current = new WebSocket("ws://localhost:8000/ws");

    socketRef.current.onopen = () => console.log("✅ WebSocket Connected");

    socketRef.current.onmessage = (event) => {
      try {
        const updatedData = JSON.parse(event.data);
        if (Array.isArray(updatedData.drones)) {
          setDroneData([...updatedData.drones]); // ✅ Force re-render
        } else {
          console.warn("⚠️ Unexpected WebSocket Data Format:", updatedData);
        }
      } catch (error) {
        console.error("❌ Error parsing WebSocket data:", event.data);
      }
    };

    socketRef.current.onerror = (error) => console.error("❌ WebSocket Error:", error);

    socketRef.current.onclose = () => {
      console.warn("⚠️ WebSocket Disconnected. Attempting Reconnection...");
      setTimeout(() => {
        socketRef.current = new WebSocket("ws://localhost:8000/ws");
      }, 3000);
    };

    return () => {
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);

  useEffect(() => console.log("🛰 Updated Drone Data:", droneData), [droneData]);

  // ✅ Marker icons for drones & restricted zones
  const getMarkerIcon = (drone) =>
    L.icon({
      iconUrl: drone.unauthorized ? redMarkerIcon : greenMarkerIcon,
      iconSize: [30, 40],
      iconAnchor: [15, 40],
      popupAnchor: [0, -35],
      className: "custom-drone-marker",
    });

  const getZoneIcon = (zoneName) => {
    let iconImage =
      zoneName.includes("Airport") ? airportIconImg :
      zoneName.includes("Base") || zoneName.includes("Fort") ? militaryIconImg :
      governmentIconImg;

    return L.icon({
      iconUrl: iconImage,
      iconSize: [40, 40],
      iconAnchor: [20, 40],
      popupAnchor: [0, -35],
    });
  };

  return (
    <div className="drone-dashboard">
      <nav className="navbar">
          <h1 className="logo">🚁 Illegal Drone Tracking System</h1>
          <div className="nav-links">
            <button className="nav-button">Live Detections</button>
            <button className="nav-button">Settings</button>
          </div>
        </nav>
      {/* 🏷️ Header */}
      <div className="header">
        🚀 <strong>Tracking Unauthorized Drone Activity in Real-Time</strong> 🛰️
      </div>

      {/* 🗺️ Full-Width Map */}
      <div className="map-container">
        <MapContainer center={[37.7749, -122.4194]} zoom={5} style={{ height: "500px", width: "100%" }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

          {restrictedZones.map((zone, index) => (
            <Marker key={index} position={[zone.latitude, zone.longitude]} icon={getZoneIcon(zone.name)}>
              <Popup>
                ⚠️ <strong>{zone.name}</strong>
                <br /> Restricted Zone - Radius: {zone.radius} km
              </Popup>
            </Marker>
          ))}

          {droneData.map((drone, index) => (
            <Marker key={index} position={[drone.latitude, drone.longitude]} icon={getMarkerIcon(drone)}>
              <Popup>
                🚁 <strong>{drone.callsign || "Unknown"}</strong>
                <br />🌍 {drone.latitude.toFixed(2)}, {drone.longitude.toFixed(2)}
                <br />📍 Altitude: {drone.altitude.toFixed(2)} meters
                <br />💨 Velocity: {drone.velocity.toFixed(2)} km/h
                <br />🔴 Restricted Area: {drone.unauthorized ? "YES 🚨" : "NO ✅"}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* 📊 Real-Time Statistics Panel BELOW the Map */}
      <StatsPanel droneData={droneData} />

      {/* 📡 Live Drone Updates */}
      <div className="drone-updates">
        <h3>📊 Live Drone Updates</h3>
        <p>
          ⚡ <strong>Avg Velocity:</strong>{" "}
          {droneData.length > 0
            ? (
                droneData.reduce((sum, drone) => sum + (drone.velocity || 0), 0) /
                droneData.length
              ).toFixed(2)
            : 0} km/h
        </p>
        <ul>
          {droneData.length > 0 ? (
            droneData.map((drone, index) => (
              <li key={index} className={drone.unauthorized ? "unauthorized-drone" : "authorized-drone"}>
                🚁 <strong>{drone.callsign || "Unknown"}</strong> - Lat: {drone.latitude?.toFixed(2) || "N/A"}, 
                Lon: {drone.longitude?.toFixed(2) || "N/A"}, Alt: {drone.altitude?.toFixed(2) || "N/A"} m, 
                Vel: {drone.velocity?.toFixed(2) || "N/A"} km/h
                {drone.unauthorized && (
                  <span style={{ color: "red", fontWeight: "bold" }}>
                    ⚠️ Unauthorized Drone Detected 🚨
                  </span>
                )}
              </li>
            ))
          ) : (
            <p style={{ color: "gray", fontStyle: "italic" }}>📡 Waiting for live drone updates...</p>
          )}
        </ul>
      </div>
    </div>
);
};

export default DroneMap;
