import React, { useState, useEffect } from "react";

const DroneValidation = () => {
  const [validationResult, setValidationResult] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchValidationData();
    const interval = setInterval(fetchValidationData, 10000); // üîÑ Updated to refresh every 10 sec
    return () => clearInterval(interval);
  }, []);

  const fetchValidationData = async () => {
    try {
      const response = await fetch("http://127.0.0.1:8000/fetch-drones-live");
      const data = await response.json();

      if (data.validation) {
        setValidationResult(data.validation);
        setError(null);
      } else {
        setError("‚ö†Ô∏è No validation data available.");
      }
    } catch (err) {
      setError("‚ùå Error fetching validation results.");
    }
  };

  return (
    <div className="drone-validation">
      <h2>üìä Drone Data Validation</h2>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {validationResult && (
        <div>
          <p><strong>üöÅ Total Drones:</strong> {validationResult.total_drones}</p>
          <p><strong>‚úÖ Authorized:</strong> {validationResult.authorized}</p>
          <p><strong>‚ùå Unauthorized:</strong> {validationResult.unauthorized}</p>
          <p><strong>‚ùì Unknown:</strong> {validationResult.unknown}</p>
          <p style={{ color: validationResult.validation_passed ? "green" : "red", fontWeight: "bold" }}>
            {validationResult.validation_passed ? "‚úî Validation Passed" : "‚ùå Validation Failed"}
          </p>
        </div>
      )}
    </div>
  );
};

export default DroneValidation;
