import React, { useState, useEffect, useRef } from 'react';
import DroneMap from './DroneMap';
import StatsPanel from './StatsPanel';
import './App.css'; // Optional, for overall app styling

const App = () => {
  const [droneData, setDroneData] = useState([]);
  const socketRef = useRef(null);

  // Initial API fetch and WebSocket setup
  useEffect(() => {
    const fetchDroneData = async () => {
      try {
        const response = await fetch("http://127.0.0.1:8000/fetch-drones-live");
        const data = await response.json();
        setDroneData(data.drones || []);
      } catch (error) {
        console.error("Error fetching drone data:", error);
      }
    };

    fetchDroneData();

    socketRef.current = new WebSocket("ws://localhost:8000/ws");

    socketRef.current.onopen = () => {
      console.log("✅ WebSocket connected");
    };

    socketRef.current.onmessage = (event) => {
      try {
        const receivedData = JSON.parse(event.data);
        if (receivedData.drones && Array.isArray(receivedData.drones)) {
          setDroneData(receivedData.drones);
        } else {
          console.warn("⚠️ Unexpected WebSocket data format:", receivedData);
        }
      } catch (error) {
        console.error("❌ Error parsing WebSocket data:", error);
      }
    };

    socketRef.current.onerror = (error) => {
      console.error("❌ WebSocket error:", error);
    };

    socketRef.current.onclose = () => {
      console.warn("⚠️ WebSocket disconnected. Reconnecting in 10s...");
      setTimeout(() => {
        socketRef.current = new WebSocket("ws://localhost:8000/ws");
      }, 10000);
    };

    return () => {
      if (socketRef.current) socketRef.current.close();
    };
  }, []);

  return (
    <div className="container">
      <DroneMap droneData={droneData} />
      <StatsPanel droneData={droneData} />
    </div>
  );
};

export default App;
