import React from "react";

const DroneUpdates = ({ droneData, lastUpdated }) => {
  return (
    <div className="drone-updates">
      <h2>üì° Live Drone Updates</h2>
      <p>üïí Last Updated: {lastUpdated || "Waiting for updates..."}</p>
      {droneData.length > 0 ? (
        <ul>
          {droneData.map((drone, index) => (
            <li
              key={index}
              className={drone.unauthorized ? "unauthorized-drone" : "authorized-drone"}
            >
              üöÅ <strong>{drone.callsign || "Unknown"}</strong> - üåç Lat: {drone.latitude}, 
              Lon: {drone.longitude} - üìè Alt: {drone.altitude} m - üí® Vel: {drone.velocity} km/h - 
              {drone.unauthorized ? " ‚ùå Unauthorized" : " ‚úÖ Authorized"}
            </li>
          ))}
        </ul>
      ) : (
        <p>üì° No live drone updates available.</p>
      )}
    </div>
  );
};

export default DroneUpdates;
