import React, { useState, useEffect } from 'react';

const StatsPanel = ({ droneData }) => {
    const [stats, setStats] = useState({
        totalDrones: 0,
        unauthorizedDrones: 0,
        avgSpeed: 0,
        avgAltitude: 0,
        restrictedZoneViolations: 0,
        lastUpdated: new Date().toLocaleTimeString(),
    });

    useEffect(() => {
        if (!droneData || droneData.length === 0) return;

        const totalDrones = droneData.length;
        const unauthorizedDrones = droneData.filter(drone => drone.unauthorized).length;

        const avgSpeed = (
            droneData.reduce((sum, drone) => sum + drone.velocity, 0) / totalDrones
        ).toFixed(2);

        const avgAltitude = (
            droneData.reduce((sum, drone) => sum + drone.altitude, 0) / totalDrones
        ).toFixed(2);

        const restrictedZoneViolations = droneData.filter(drone => drone.inRestrictedZone).length;

        setStats({
            totalDrones,
            unauthorizedDrones,
            avgSpeed,
            avgAltitude,
            restrictedZoneViolations,
            lastUpdated: new Date().toLocaleTimeString(),
        });
    }, [droneData]);

    return (
        <div className="stats-panel">
            <h2>üìä Real-Time Drone Statistics</h2>
            <p>‚è≥ Last Updated: {stats.lastUpdated}</p>
            <ul>
                <li>üöÅ Total Drones: {stats.totalDrones}</li>
                <li>üö® Unauthorized Drones: {stats.unauthorizedDrones}</li>
                <li>üìà Avg Speed: {stats.avgSpeed} km/h</li>
                <li>‚õ∞Ô∏è Avg Altitude: {stats.avgAltitude} m</li>
                <li>üèõÔ∏è Restricted Zone Violations: {stats.restrictedZoneViolations}</li>
            </ul>
        </div>
    );
};

export default StatsPanel;
